set password = #{password,jdbcType=VARCHAR},
  phone = #{phone,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, password, phone
from user
where id = #{id,jdbcType=VARCHAR}

